mapper.delete:

	DELETE FROM private_qna
 
 
 
 	private_qna_num = #{private_qna_num}
 	AND board_num = #{board_num}

mapper.update:

		UPDATE private_qna
		SET private_qna_content=#{private_qna_content}, private_qna_title=#{private_qna_title}, private_qna_date=sysdate
 
 
 
		private_qna_num=#{private_qna_num}
		AND board_num=#{board_num }
