mapper.select:
    
		SELECT t.*
		FROM
		(
		    SELECT chat_no,sender,receiver,contents,send_date,'true' me_at
		    FROM chat_list
		    WHERE sender=#{sender}
		    AND receiver=#{receiver}
		    UNION ALL
		    SELECT chat_no,sender,receiver,contents,send_date,'false' me_at
		    FROM chat_list
		    WHERE sender=#{receiver}
		    AND receiver=#{sender}
		) t
		ORDER BY chat_no
