mapper.select:


         select flea_code, memberid, flea_name, intro_cotent, flea_like_count, utl_raw.cast_to_varchar2(profile_photo), utl_raw.cast_to_varchar2(main_photo)
         from flea



				flea_code = #{p_id}



         select id, pwd, name, email, joinDate
         from   t_member



				id = #{p_id}

mapper.update:
update t_member
set  pwd = #{pwd}
   , name = #{name}
   , email = #{email}
where id = #{id}
