mapper.delete:

	DELETE FROM prodQnA
 
 
 
 	prod_number = #{prod_number}
 	AND qna_number = #{qna_number}

mapper.select:
SELECT prod_number, qna_number, qna_content, TO_CHAR(qna_date, 'yyyy-mm-dd HH24:MI') as qna_date, memberId, replace(memberId, substr(memberId,-3), '***') as secretMember, answer_yn, TO_CHAR(answer_date, 'yyyy-mm-dd HH24:MI') as answer_date, answer_content, secret_yn FROM prodQnA
prod_number = #{prod_number}
ORDER BY qna_number DESC

mapper.update:
	
		UPDATE prodQnA
		SET answer_yn='y', answer_content=#{answer_content}, answer_date=sysdate
	 
	 

 	
 		prod_number=#{prod_number}
		AND qna_number=#{qna_number}

	
		UPDATE prodQnA
		SET answer_yn = 'n', answer_content = null, answer_date = null
	 
	 
 	
 		prod_number=#{prod_number}
		AND qna_number=#{qna_number}

mapper.select:
 
      
         SELECT qna_number, answer_content, TO_CHAR(answer_date, 'yyyy-mm-dd HH24:MI') as answer_date
         FROM   prodQnA
      
		
		
		
			prod_number = #{prod_number}
			AND qna_number = #{qna_number}
